import UIKit

class MovieDetailsWorker
{
    func list(movie: Movie, name: ListName, action: Action, onSuccess: (ListName, Status, Action)->Void){
        LocalWorker().list(do: action, movie: movie, in: name, onSuccess: onSuccess)
    }
}
